def calculate_additional_cost(plan, current_storage, required_storage):
    # Define the storage limits and prices for each plan
    plans = {
        'Standard': {'price': 9.99, 'storage': 2},
        'Plus': {'price': 19.99, 'storage': 10},
        'Premium': {'price': 39.99, 'storage': 50}
    }

    # Get the current plan details
    current_plan = plans[plan]

    # Calculate additional cost if required storage exceeds current plan storage
    if required_storage > current_plan['storage']:
        additional_cost = 0
        for p in plans:
            if plans[p]['storage'] >= required_storage:
                additional_cost = plans[p]['price'] - current_plan['price']
                break
        return additional_cost
    else:
        return 0

# Example usage
current_plan = 'Standard'
current_storage = 2  # TB
required_storage = 10  # TB
additional_cost = calculate_additional_cost(current_plan, current_storage, required_storage)
print(f'Additional cost for upgrading: ${additional_cost:.2f}')
