def caesar_decrypt(encoded_message, shift):
    decrypted_message = ""
    for char in encoded_message:
        if char.isalpha():
            shift_base = ord('A') if char.isupper() else ord('a')
            decrypted_char = chr((ord(char) - shift_base - shift) % 26 + shift_base)
            decrypted_message += decrypted_char
        else:
            decrypted_message += char
    return decrypted_message

def find_shift_and_decrypt(encoded_message):
    for shift in range(1, 26):
        decrypted_message = caesar_decrypt(encoded_message, shift)
        print(f'Shift: {shift}, Decrypted Message: {decrypted_message}')

# Example usage:
encoded_message = "Wklv lv d whvw phvvdjh."
find_shift_and_decrypt(encoded_message)
